import React from 'react'
import api from '../../http/index';
import './index.css'
import ErrorTips from './error'
const { getMessage } = api;
class GetQQ extends React.Component{
    state = {
        qlogo:'',
        name:'',
        qq:'',
        loading:true, // 加载中提示
        errorTips:false
    }
    // dounce = (fn:Function,time:number)=>{
    //     let timer:any=null
    //     return function(){
    //         if(timer) clearTimeout(timer)
    //         timer = setTimeout(()=>{
    //             fn()
    //         },time)
    //     }
    // }   
    qqChange = async (e:any)=>{  
        this.setState({
            loading:true
        })
        const value = e.target.value
        const res = await getMessage({qq:value})
        const {code,name,qlogo,qq} = res
        console.log(res)
        if(code===1){
            this.setState({
                qlogo,
                name,
                qq,
                loading:false,
                errorTips:false
            })
        } else {
            this.setState({
                loading:true,
                errorTips:true
            })
        }
    }
    // 检验QQ号是否合法
    checkQQByRegular =(s:string)=>{
        return(/^[1-9]\d{4,9}$/.test(s)) ? true : false;
    }
    render() {
        const {qlogo,name,qq,loading,errorTips} = this.state
        return(
            <div className="content_wrap">
                <h1> QQ号查询 </h1>
                <div className="qq_number">
                    <span>QQ</span>
                    <input type="text" className="qq_border" placeholder='输入qq号' onChange = { this.qqChange }/>
                </div>
                {
                    !loading &&!errorTips?<div className="qq_wrap">
                    <img src={qlogo} alt="" className="img_name"/>
                    <div>
                        <div className='qq_name'>{name}</div>
                        <div className='qq_item'>{qq}</div>
                    </div>
                 </div>:null

                }


                {/* {
                   !loading && ! errorTips? null:loading && ! errorTips?<span>加载中...</span>:
                   loading && errorTips?<ErrorTips/>:<div className="qq_wrap">
                   <img src={qlogo} alt="" className="img_name"/>
                   <div>
                       <div className='qq_name'>{name}</div>
                       <div className='qq_item'>{qq}</div>
                   </div>
                </div>
                } */}
            </div>
            )
        }
}
export default GetQQ
