import React from 'react'
import api from '../../http/index';
const { getMessage } = api;
class GetQQ extends React.Component{
    state = {
        headPortrait:'',
        nickName:'',
        qqNumber:''
    }
    qqChange = async (e:any)=>{  
        const flag = this.checkQQByRegular(e.target.value)
        if(flag){
        const res = await getMessage({qq:e.target.value})
        console.log(res,'res')
        } else {
        console.log('请输入合法的qq号')
        }
    }
    // 检验QQ号是否合法
    checkQQByRegular =(s:string)=>{
        return(/^[1-9]\d{4,9}$/.test(s)) ? true : false;
    }
    render() {
     return(
        <div>
          <h1> QQ号查询 </h1>
          <div>
            <span>QQ</span>
            <input type="text" onChange = { this.qqChange }/>
          </div>
          <div>
            <img src="" alt="" />
            <div>
              <span>叛逆宝贝</span>
              <span>12492357</span>
            </div>
          </div>
        </div>
        )
    }
}
export default GetQQ
