import React from 'react'
import api from '../../http/index';
import './index.css'
const { getMessage } = api;
class GetQQ extends React.Component{
    state = {
        qlogo:'',
        name:'',
        qq:''
    }
    qqChange = async (e:any)=>{  
        const value = e.target.value
        const flag = this.checkQQByRegular(value)
        if(flag){
            const res = await getMessage({qq:value})
            const {code,name,qlogo,qq} = res
            if(code==1){
                this.setState({
                    qlogo,
                    name,
                    qq
                })
            }
        } else {
            console.log('请输入合法的qq号')
        }
    }
    // 检验QQ号是否合法
    checkQQByRegular =(s:string)=>{
        return(/^[1-9]\d{4,9}$/.test(s)) ? true : false;
    }
    render() {
        const {qlogo,name,qq} = this.state
        return(
            <div className="content_wrap">
                <h1> QQ号查询 </h1>
                <div className="qq_number">
                    <span>QQ</span>
                    <input type="text" onChange = { this.qqChange }/>
                </div>
                <div className="qq_wrap">
                    <img src={qlogo} alt="" />
                    <div>
                    <span>{name}</span>
                    <span>{qq}</span>
                    </div>
                </div>
            </div>
            )
        }
}
export default GetQQ
